function createfigure(X1, Y1,d)
%CREATEFIGURE(X1, Y1)
%  X1:  scatter x
%  Y1:  scatter y


%  Auto-generated by MATLAB on 27-Apr-2016 18:04:25

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1);
hold(axes1,'on');

% Create scatter
scatter1 = scatter(X1,Y1,'DisplayName','data1','Marker','.');

% Get xdata from plot
xdata1 = get(scatter1, 'xdata');
% Get ydata from plot
ydata1 = get(scatter1, 'ydata');
% Make sure data are column vectors
xdata1 = xdata1(:);
ydata1 = ydata1(:);


% Remove NaN values and warn
nanMask1 = isnan(xdata1(:)) | isnan(ydata1(:));
if any(nanMask1)
    xdata1(nanMask1) = [];
    ydata1(nanMask1) = [];
end

% Find x values for plotting the fit based on xlim
axesLimits1 = xlim(axes1);
xplot1 = linspace(axesLimits1(1), axesLimits1(2));
fitResults1 = polyfit(xdata1,ydata1,d);% Find coefficients for polynomial 
yplot1 = polyval(fitResults1,xplot1);
fitLine1 = plot(xplot1,yplot1,'DisplayName','   10th degree',...% Plot the fit
    'Tag','10th degree',...
    'Parent',axes1);

% Set new line in proper position
setLineOrder(axes1,fitLine1,scatter1);

% Create xlabel
xlabel('Inches');

% Create title
title('Shape of the Track');

% Create ylabel
ylabel('Inches');

% Uncomment the following line to preserve the X-limits of the axes
% xlim(axes1,[0 86.7840637831625]);
% Uncomment the following line to preserve the Y-limits of the axes
% ylim(axes1,[-16.4953869915198 0]);
% Create legend
legend(axes1,'show');

%-------------------------------------------------------------------------%
function setLineOrder(axesh1, newLine1, associatedLine1)
%SETLINEORDER(AXESH1,NEWLINE1,ASSOCIATEDLINE1)
%  Set line order
%  AXESH1:  axes
%  NEWLINE1:  new line
%  ASSOCIATEDLINE1:  associated line

% Get the axes children
hChildren = get(axesh1,'Children');
% Remove the new line
hChildren(hChildren==newLine1) = [];
% Get the index to the associatedLine
lineIndex = find(hChildren==associatedLine1);
% Reorder lines so the new line appears with associated data
hNewChildren = [hChildren(1:lineIndex-1);newLine1;hChildren(lineIndex:end)];
% Set the children:
set(axesh1,'Children',hNewChildren);

